import { Component, EventEmitter, Output } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from '../../service/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  loginForm: FormGroup;
  invalidLogin = false;
  error = '';
  @Output() onSignUpClick = new EventEmitter(); // Event emitter for switching to sign-up

  constructor(private formBuilder: FormBuilder, private authService: AuthService, private router: Router) {
    this.loginForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });
  }

  // Easy access to form fields
  get f() { return this.loginForm.controls; }

  onSubmit(): void {

    // Reset error on new submission
    this.error = '';

    if (this.loginForm.invalid) {
      this.invalidLogin = true;
      return;
    }

    this.authService.login(this.f.email.value, this.f.password.value)
      .subscribe(
        success => {
          if (success) {
            this.authService.setLoggedIn(true);
            this.router.navigate(['/car-list']);
          }
          // Handle successful login// Adjust as per your routing setup
        },
        error => {
          // Handle error
          this.error = 'Invalid login credentials'; // Consider using a more dynamic error message
          this.invalidLogin = true; // Reset submission status
        }
      );
  }

  switchToSignUp() {
    this.onSignUpClick.emit(); // Emit event to switch to sign-up
  }
}
<div class="login-container">
  <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="email">Email</label>
      <input type="email" id="email" formControlName="email" class="form-control" [ngClass]="{ 'is-invalid': invalidLogin && f.email.errors }">
      <div *ngIf="invalidLogin && f.email.errors" class="invalid-feedback">
        <div *ngIf="f.email.errors.required">Email is required</div>
        <div *ngIf="f.email.errors.email">Email must be a valid email address</div>
      </div>
    </div>

    <div class="form-group">
      <label for="password">Password</label>
      <input type="password" id="password" formControlName="password" class="form-control" [ngClass]="{ 'is-invalid': invalidLogin && f.password.errors }">
      <div *ngIf="invalidLogin && f.password.errors" class="invalid-feedback">
        <div *ngIf="f.password.errors.required">Password is required</div>
      </div>
    </div>

    <div class="form-group">
      <button type="submit" class="btn btn-primary">Login</button>
      <button type="button" class="btn" *ngIf="invalidLogin" (click)="switchToSignUp()">Sign Up</button>
    </div>

    <div *ngIf="error" class="alert alert-danger">
      {{ error }}
    </div>
  </form>
</div>
